function createSearchboxControl() {
    return L.Control.extend({
        options: {
            position: "topleft"
        },
        onAdd: function() {
            let div = L.DomUtil.create("div");
            div.id = "controlcontainer";
            div.innerHTML = getControlHrmlContent()
            L.DomEvent.disableClickPropagation(div);
            return div
        }
    })
};